        <bl-button
          variant="tertiary"
          kind="neutral"
          size="medium"
          icon="search"
          aria-label="Search documentation (Cmd+K)"
          @bl-click="showSearch = true"
        >
          <span class="hidden sm:inline">Search</span>&nbsp;&#8984;K
        </bl-button>

        <!-- Theme Toggle -->
        <bl-button
          v-if="!dark"
          variant="tertiary"
          kind="neutral"
          size="medium"
          icon="star_fill"
          aria-label="Switch to dark mode"
          @bl-click="dark = true"
        />
        <bl-button
          v-else
          variant="tertiary"
          kind="neutral"
          size="medium"
          icon="light_bulb"
          aria-label="Switch to light mode"
          @bl-click="dark = false"
        />

